var rooms []room = []room{
	room{0, "stage", 1, []int{1}, "1a"},
	room{1, "mainhall", 7, []int{2, 3, 4, 5, 6, 7, 9}, "1b"},
	room{2, "spareparts", 2, []int{1, 7}, "3"},
	room{3, "bathrooms", 1, []int{1}, "7"},
	room{4, "piratecove", 3, []int{1, 6, 11}, "1c"},
	room{5, "kitchen", 1, []int{1}, "6"},
	room{6, "hall1", 4, []int{1, 4, 7, 8}, "2a"},
	room{7, "jenitorsroom", 2, []int{6, 8}, "5"},
	room{8, "nearoffice1", 1, []int{11}, "2b"},
	room{9, "hall2", 2, []int{10, 1}, "4a"},
	room{10, "nearoffice2", 2, []int{6, 12}, "4b"},
	room{11, "critroom1", 1, []int{13}, "-"},
	room{12, "critroom2", 1, []int{13}, "-"},
	room{13, "office", 6, []int{6, 9, 1, 4, 6, 10}, "-"},
}

var number_of_connections = 0

func main() {
	rand.Seed(time.Now().UTC().UnixNano())

	//startint the server

	port := flag.String("port", "8082", "port to listen on")
	flag.Parse()

	l, err := net.Listen("tcp", ":"+*port)
	if err != nil {
		fmt.Println("Unable to start server at port " + *port)
		return
	}

	defer l.Close()
	myFigure := figure.NewColorFigure("Five nights", "shadow", "green", true)
	myFigure.Print()
	myFigure1 := figure.NewColorFigure("               at Freddy's", "sblood", "red", true)
	myFigure1.Print()
	myFigure2 := figure.NewFigure("                                 SERVER", "", true)
	myFigure2.Print()

	fmt.Println("Server has started on port " + *port)
	for {
		conn, err := l.Accept()
		if err != nil {
		}
		number_of_connections++
		fmt.Println(fmt.Sprintf("current connections: %d", number_of_connections))
		go getinput(conn)
	}

}

